import { Component } from '@angular/core';
import { MenuItem } from './app.interfaces';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'ExerciseTracker';

  usersMenu: MenuItem[] = [
    { text: 'List all Users', link: 'user/list'},
    { text: 'Create a User', link: 'not-implemented-yet'}
  ];
  exercisesMenu: MenuItem[] = [
    { text: 'List all exercises', link: 'exercises/list'},
    { text: 'Insert an exercise', link: 'not-implemented-yet'}
  ];
}
